mapper.update:
update sys_depart
`parent_id`=#{parentId,jdbcType=VARCHAR}, depart_name=#{departName,jdbcType=VARCHAR}, depart_name_py=#{departNamePy,jdbcType=VARCHAR}, depart_name_abbr=#{departNameAbbr,jdbcType=VARCHAR}, pay_code=#{payCode,jdbcType=VARCHAR}, pay_channel=#{payChannel,jdbcType=TINYINT}, org_category=#{orgCategory,jdbcType=TINYINT}, org_code=#{orgCode,jdbcType=VARCHAR}, memo=#{memo,jdbcType=VARCHAR},
WHERE id =#{id,jdbcType=VARCHAR}

mapper.select:
	   select * from sys_depart where 1=1

            AND (depart_name like '%${departName}%' or depart_name_py like '%${departNamePy}%')



            AND id in

                #{id}


        AND org_category= #{orgCategory}
        and del_flag != '1'

select * from sys_depart where 1=1

    AND parent_id = #{parentId}



    AND depart_name = #{departName}

and del_flag != '1' limit 1
