mapper.update:
update sys_permission
`name`=#{name,jdbcType=VARCHAR}, parent_id=#{parentId,jdbcType=VARCHAR}, resource_type=#{resourceType,jdbcType=VARCHAR}, url=#{url,jdbcType=VARCHAR}, sort_no=#{sortNo,jdbcType=DOUBLE}, component=#{component,jdbcType=VARCHAR}, permission=#{permission,jdbcType=VARCHAR}, is_leaf=#{isLeaf,jdbcType=TINYINT}, del_flag = #{delFlag,jdbcType=TINYINT},
WHERE id =#{id,jdbcType=VARCHAR}

mapper.select:
SELECT p.*
FROM  sys_permission p
WHERE (exists(
select a.role_id from sys_role_permission a
join sys_role b on a.role_id = b.id
join sys_user_role c on c.role_id = b.id
join sys_user d on d.id = c.user_id
where p.id = a.permission_id AND d.username = #{username,jdbcType=VARCHAR}
)
)

SELECT id ,parent_id ,`name` ,is_leaf FROM sys_permission WHERE 1=1
AND parent_id = #{parentId,jdbcType=VARCHAR}
AND parent_id is null

select * from `sys_permission` where `id` in
#{id}
and del_flag != '1' order by `sort_no` ASC
